import React, { Component } from "react";

class WorkForm extends Component {
    constructor() {
        super();
        this.changeDisplay = this.changeDisplay.bind(this);
        this.state = {
          displayForm : true,
        };
    }

    changeDisplay = (e) => {
        e.preventDefault();
        this.setState({
            displayForm: false,
            schoolName: "",
            degree: "",
            major: "",
            from: "",
            to: "",
          });
    }

    render(){
        const displayForm = this.state.displayForm;
        if(displayForm){
            return (
            <div>
                <form className = "education-form">
                    <div className = "form-section">
                        <input type = "text" placeholder = "Company Name"></input>
                        <input type = "text" placeholder = "Position/Role"></input>
                    </div>
                    <div className = "form-section">
                        <input type = "text" placeholder = "From"></input>
                        <input type = "text" placeholder = "To"></input>
                    </div>
                    <div className = "form-section">
                        <textarea placeholder = 'Description of tasks(Separate using "Enter" key)' className = "width-100" rows = "3"></textarea>
                    </div>
                    <button onClick = {this.changeDisplay} className = "delete-button">Delete</button>
                </form>
            </div>
            );
        }
    }
}

export default WorkForm;
