import React, { Component } from "react";
import PersonalInfo from "./components/personal-info";
import Header from "./components/header";
import "./styles/style.css";
import EducationalExperience from "./components/educational-experience";
import WorkExperience from "./components/work-experience";


class App extends Component {
  constructor() {
    super();
    this.changeFormStatus = this.changeFormStatus.bind(this);
    this.state = {
      formSubmitted : false,
    };
  }

  changeFormStatus = (e) => {
    e.preventDefault();
    this.setState({
        formSubmitted: !this.state.formSubmitted,
      });
}

  render() {
    const formSubmitted = this.state.formSubmitted;
    if(formSubmitted){
      console.log(formSubmitted);
      return(
        <div>
          <PersonalInfo formSubmitted = {this.state.formSubmitted}/>
        </div>
      );
    }
    else{
      return (
        <div className = "main">
          <Header />
          <PersonalInfo formSubmitted = {this.state.formSubmitted}/>
          <EducationalExperience />
          <WorkExperience />
          <button onClick = {this.changeFormStatus} className = "to-cv-button">Convert to CV</button>
        </div>
      );
    }
  }
}

export default App;
