import React, { Component } from "react";
import WorkForm from "./work-form";

class WorkExperience extends Component {
    constructor(props) {
        super(props);
        this.addExperience = this.addExperience.bind(this);
        this.state = {
          experiencesList : [],
          formSubmitted : this.props.formSubmitted,
        };
    }

    addExperience = (e) => {
        e.preventDefault();
        this.setState({
            experiencesList: this.state.experiencesList.concat(<WorkForm formSubmitted = {this.state.formSubmitted}/>),
          });
    }
    render(){
            console.log(this.state.formSubmitted);
            return (
                <div className = "experience">
                    <h2>Work Experience</h2>
                    {this.state.experiencesList}
                    <button className = "add-button" onClick = {this.addExperience}>Add Experience</button>
                </div>
            );
    }
}

export default WorkExperience;
